//CRUD - EDIT

$app->post('/pedidos/:id',	function ($id) use ($app){

	$body = json_decode($app->request()->getBody());
	
	$params = array (
		'id'	=> $id,
		'direccion'	    => isset($body->direccion)?$body->direccion:'',
		'estado'	    => isset($body->estado)?$body->estado:'',
		'fecha'	    => isset($body->fecha)?$body->fecha:'',
		'id_cliente'	    => $body->id_cliente,
		'id_producto'	    => $body->id_producto,
		'total'	    => isset($body->total)?$body->total:''	);

	$obj = makeQuery("UPDATE pedidos SET  direccion = :direccion,  estado = :estado,  fecha = :fecha,  id_cliente = :id_cliente,  id_producto = :id_producto,  total = :total   WHERE _id = :id LIMIT 1", $params, false);

	
	echo json_encode($body);
    	
});


